import React from 'react';

function Metrics({ metrics, streak, resetBadges, badgeCounts }) {
  const { completedTasks } = metrics;

  const badgeLevels = {
    Bronze: 5,
    Silver: 15,
    Gold: 30,
    Platinum: 50,
  };

  let currentBadge = null;
  let nextBadge = null;

  if (completedTasks >= badgeLevels.Platinum) {
    currentBadge = "Platinum";
  } else if (completedTasks >= badgeLevels.Gold) {
    currentBadge = "Gold";
    nextBadge = "Platinum";
  } else if (completedTasks >= badgeLevels.Silver) {
    currentBadge = "Silver";
    nextBadge = "Gold";
  } else if (completedTasks >= badgeLevels.Bronze) {
    currentBadge = "Bronze";
    nextBadge = "Silver";
  } else {
    nextBadge = "Bronze";
  }

  const nextBadgeProgress = nextBadge
    ? ((completedTasks / badgeLevels[nextBadge]) * 100).toFixed(0)
    : 100;

  return (
    <section className="metrics bg-gradient-to-br from-white to-gray-100 p-6 rounded-lg shadow-lg text-black w-full max-w-md">
      <h2 className="text-xl font-bold mb-4">Metrics</h2>
      <p><strong>Completed Tasks:</strong> {completedTasks}</p>
      <p><strong>Current Streak:</strong> {streak} days</p>
      
      {currentBadge && (
        <div className="mt-4 p-4 bg-gradient-to-r from-yellow-400 to-orange-500 text-white rounded-lg shadow">
          <p className="font-bold text-center">You earned a {currentBadge} Badge!</p>
        </div>
      )}

      {nextBadge && (
        <div className="mt-4">
          <p><strong>Next Badge:</strong> {nextBadge}</p>
          <div className="relative w-full bg-gray-200 rounded-full h-4">
            <div
              className="absolute bg-green-500 h-4 rounded-full"
              style={{ width: `${nextBadgeProgress}%` }}
            ></div>
          </div>
          <p className="text-sm mt-1 text-center">{nextBadgeProgress}% to {nextBadge} Badge</p>
        </div>
      )}

      <div className="mt-6">
        <h3 className="text-lg font-bold mb-2">Badge History</h3>
        <ul className="list-disc pl-6">
          {Object.keys(badgeCounts).map((badge) => (
            <li key={badge}>
              {badge}: {badgeCounts[badge]} times
            </li>
          ))}
        </ul>
      </div>

      {completedTasks >= badgeLevels.Platinum && (
        <button
          onClick={resetBadges}
          className="mt-6 w-full py-2 bg-red-500 text-white font-bold rounded-lg shadow-lg hover:shadow-xl"
        >
          Reset Badge Progress
        </button>
      )}
    </section>
  );
}

export default Metrics;
