import React, { useState } from 'react';
import Header from './components/Header';
import Timer from './components/Timer';
import Footer from './components/Footer';
import Soundscapes from './components/Soundscapes';
import Metrics from './components/Metrics';
import SavedTasks from './components/SavedTasks';

function App() {
  const [metrics, setMetrics] = useState({
    completedTasks: 0,
    totalFocusTime: 0,
    totalBreaks: 0,
  });

  const [savedTasks, setSavedTasks] = useState([]);

  return (
    <div className="app-container bg-gradient-to-r from-purple-500 via-blue-500 to-indigo-600 min-h-screen text-white flex flex-col">
      <Header />
      <main className="p-6 flex-1 flex flex-col items-center gap-6">
        <Timer metrics={metrics} setMetrics={setMetrics} savedTasks={savedTasks} />
        <SavedTasks savedTasks={savedTasks} setSavedTasks={setSavedTasks} />
        <Soundscapes />
        <Metrics metrics={metrics} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
