import React, { useState } from 'react';
import ReactAudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

function Soundscapes() {
  const [currentSound, setCurrentSound] = useState('');

  const soundOptions = [
    { name: 'Rain', url: '/sounds/rain.mp3' },
    { name: 'White Noise', url: '/sounds/white-noise.mp3' },
    { name: 'Lo-fi Beats', url: '/sounds/lofi.mp3' },
  ];

  return (
    <section className="soundscapes p-4">
      <h2 className="text-xl mb-4">Focus Soundscapes</h2>
      <select
        className="border p-2 rounded mb-4 w-full"
        onChange={(e) => setCurrentSound(e.target.value)}
        value={currentSound}
      >
        <option value="">Select a soundscape</option>
        {soundOptions.map((sound) => (
          <option key={sound.name} value={sound.url}>
            {sound.name}
          </option>
        ))}
      </select>
      {currentSound && (
        <ReactAudioPlayer
          src={currentSound}
          autoPlay
          controls
        />
      )}
    </section>
  );
}

export default Soundscapes;
