import React, { useState, useEffect } from 'react';

function Timer({ defaultTime, onFocusComplete }) {
  const [time, setTime] = useState(defaultTime * 60);
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    setTime(defaultTime * 60);
  }, [defaultTime]);

  useEffect(() => {
    if (isRunning) {
      const interval = setInterval(() => {
        setTime((prevTime) => {
          if (prevTime === 1) {
            setIsRunning(false);
            onFocusComplete();
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [isRunning, onFocusComplete]);

  const toggleTimer = () => {
    setIsRunning(!isRunning);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setTime(defaultTime * 60);
  };

  return (
    <section className="timer text-center p-6 bg-white rounded-lg shadow-lg text-black w-full max-w-md">
      <h2 className="text-xl font-bold mb-4">Focus Timer</h2>
      <div className="text-5xl font-mono mb-6">
        {Math.floor(time / 60)}:{String(time % 60).padStart(2, '0')}
      </div>
      <div className="flex justify-center space-x-4">
        <button
          className={`py-2 px-6 rounded ${
            isRunning ? 'bg-red-500' : 'bg-green-500'
          } text-white font-bold`}
          onClick={toggleTimer}
        >
          {isRunning ? 'Pause' : 'Start'}
        </button>
        <button
          className="py-2 px-6 rounded bg-gray-500 text-white font-bold"
          onClick={resetTimer}
        >
          Reset
        </button>
      </div>
    </section>
  );
}

export default Timer;
