import React, { useState } from 'react';

function SavedTasks({ savedTasks, setSavedTasks }) {
  const [taskInput, setTaskInput] = useState('');
  const [taskTime, setTaskTime] = useState(25);

  const addTask = () => {
    if (taskInput.trim()) {
      setSavedTasks([...savedTasks, { name: taskInput.trim(), time: taskTime }]);
      setTaskInput('');
      setTaskTime(25);
    }
  };

  return (
    <section className="saved-tasks bg-white p-6 rounded-lg shadow-lg text-black w-full max-w-md">
      <h2 className="text-xl font-bold mb-4">Saved Tasks</h2>
      <div className="flex flex-col gap-2">
        <input
          type="text"
          placeholder="Task Name"
          value={taskInput}
          onChange={(e) => setTaskInput(e.target.value)}
          className="p-2 border rounded"
        />
        <input
          type="number"
          min="1"
          max="120"
          value={taskTime}
          onChange={(e) => setTaskTime(Number(e.target.value))}
          className="p-2 border rounded"
          placeholder="Time (in minutes)"
        />
        <button
          onClick={addTask}
          className="py-2 px-4 bg-blue-500 text-white rounded"
        >
          Save Task
        </button>
      </div>
      <ul className="mt-4 list-disc pl-6">
        {savedTasks.map((task, index) => (
          <li key={index}>
            {task.name} - {task.time} min
          </li>
        ))}
      </ul>
    </section>
  );
}

export default SavedTasks;
