import React, { useState } from 'react';
import Header from './components/Header';
import Timer from './components/Timer';
import Tasks from './components/Tasks';
import Footer from './components/Footer';
import Soundscapes from './components/Soundscapes';
import PositiveReinforcement from './components/PositiveReinforcement';

function App() {
  const [defaultTime, setDefaultTime] = useState(25); // Default focus time in minutes
  const [showReinforcement, setShowReinforcement] = useState(false);

  return (
    <div className="app-container bg-gradient-to-r from-purple-500 via-blue-500 to-indigo-600 min-h-screen text-white flex flex-col">
      <Header />
      <main className="p-6 flex-1 flex flex-col items-center">
        <Timer
          defaultTime={defaultTime}
          onFocusComplete={() => setShowReinforcement(true)}
        />
        {showReinforcement && (
          <PositiveReinforcement onClose={() => setShowReinforcement(false)} />
        )}
        <Soundscapes />
        <div className="w-full max-w-md mt-6">
          <label htmlFor="default-time" className="block text-sm font-bold mb-2">
            Set Default Focus Time (minutes)
          </label>
          <input
            id="default-time"
            type="number"
            min="1"
            max="60"
            value={defaultTime}
            onChange={(e) => setDefaultTime(Number(e.target.value))}
            className="w-full p-2 rounded bg-white text-black"
          />
        </div>
        <Tasks />
      </main>
      <Footer />
    </div>
  );
}

export default App;
