import React, { useState, useEffect } from 'react';

function Timer({ onFocusComplete }) {
  const [time, setTime] = useState(1500); // 25 minutes in seconds
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    if (isRunning) {
      const interval = setInterval(() => {
        setTime((prevTime) => {
          if (prevTime === 1) {
            setIsRunning(false);
            onFocusComplete();
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [isRunning, onFocusComplete]);

  const toggleTimer = () => {
    setIsRunning(!isRunning);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setTime(1500);
  };

  return (
    <section className="timer text-center p-4">
      <h2 className="text-xl mb-4">Focus Timer</h2>
      <div className="text-4xl font-mono my-4">
        {Math.floor(time / 60)}:{String(time % 60).padStart(2, '0')}
      </div>
      <button
        className={`py-2 px-4 rounded ${
          isRunning ? 'bg-red-500' : 'bg-green-500'
        } text-white`}
        onClick={toggleTimer}
      >
        {isRunning ? 'Pause' : 'Start'}
      </button>
      <button
        className="py-2 px-4 rounded bg-gray-500 text-white ml-4"
        onClick={resetTimer}
      >
        Reset
      </button>
    </section>
  );
}

export default Timer;
